    //Objc function related to the countdown seconds functionality..
    @objc func TimerClass ()
    {
        seconds = seconds - 1
        CountDownLabel.text = String (seconds)
        
        if (seconds == 0)
        {

            userDefaultsReference.userDefaults.set(setWorkOutCounter, forKey: "SetsCompleted")
            setWorkOutCounter += 1
            setWorkOutCounterReference = setWorkOutCounter
            userDefaultsReference.userDefaults.set(setWorkOutCounter, forKey: "SetsCompleted")
            
            //Saving second time
            secondsCountReference = seconds
            secondsCounterReference = seconds
            userDefaultsReference.userDefaults.set(secondsCounterReference, forKey: "AmountOfSeconds")
            
                        
            //..Suppose to be 25
            seconds = 10
            //Testing
            CountDownLabel.text = String(seconds)
            
            timer.invalidate()
        }
        
    }
    
    // MARK: - Random Interestial Ads
    //Randomizer for showing the interestial Ads
    func randomInterestialAds() {
        desiredRandomNumber = 2
        var randomNumber = Int.random(in: 0...4)
        
        //In order to show the Ads, the number needs to be: 2
        if(desiredRandomNumber == randomNumber)
        {
            if(isPurchased()){
              
                //Do not display any Ads. Remove All Interestial Ads.
                print("User already removed the Ads")
                
            } 
            else{
                
            //Showing Interstial Ads to the user, User haven't removed all Ads;
            showInterstitial()
                
            }
        }
        else {
            
            desiredRandomNumber = 0
        }
        
    }
    
    //..Function to take you to your next push up set: (Once the rest period of time reachs 0 seconds, it will let you move forward)
    @IBAction func NextSetButton(_ sender: UIButton)
    {
        
        if let value = userDefaultsReference.userDefaults.value(forKey: "InitialWorkout")
        {
            resumeUserPushUpProgram()
        }
        
        else{
     
            pushUpProgram()
        }
        
    }
    
    //Showing Interestial Ads if there's one already available;
    func showInterstitial (){
        if(interstitial.isReady){
            interstitial.present(fromRootViewController: self)
            //Equal to create/load function below
            interstitial = createAd()
        }
    }
    
    //Tells the delegate an ad request loaded an Ad.
    func adViewDidReceiveAd(_ bannerView: GADBannerView) {
        
        //banner.isHidden = false
        
        //If the user already bought the item to remove all ads, hide the banner
        if(isPurchased()) {
            banner.isHidden = true
        }
        else{
            banner.isHidden = false
        }
        
    }
    
    //Tells the delegate an ad request failed
    func adView(_ bannerView: GADBannerView, didFailToReceiveAdWithError error: GADRequestError) {
        
        banner.isHidden = true
        
        //If the user already bought the item to remove all ads, hide the banner
        if(isPurchased()) {
            banner.isHidden = true
            
        }
    }
    // If the application has been bought before;
     func isPurchased() -> Bool {
         let purchasesStatus = userDefaultsReference.userDefaults.bool(forKey: productID)
//        let purchasesStatus = purchasesSavingData.bool(forKey: productID)
        if purchasesStatus {
            print("Previously Purchased")
            return true
            
            //..Whether Shows Ads or Not;
        }
        
        else{
            print("Never Purchased")
            return false
        }
        
    }
    
    func showingBannerAds() {
        
        let removeAllAdsPurchase = userDefaultsReference.userDefaults.bool(forKey: productID)
//        let removeAllAdsPurchase = purchasesSavingData.bool(forKey: productID)
        if(removeAllAdsPurchase)
        {
            //If its true, remove all Ads
            banner.isHidden = true
        }
        else {
            banner.isHidden = false
            
        }
    }

    
    func removingAllAds(){
    
        userDefaultsReference.userDefaults.set(true, forKey: productID)
//    purchasesSavingData.set(true, forKey: productID)
        
    }
    
    //Create/Load another secondary interstial Ads after the first has been displayed;
    func createAd() -> GADInterstitial {
        let inter = GADInterstitial(adUnitID: "ca-app-pub-3187572158588519/5068250698")
        inter.load(GADRequest())
        return inter
    }
    

